import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
    const [form, setForm] = useState({
      name: "",
      position: "",
      level: "",
      rut: "",
    });
    const navigate = useNavigate();
  
    function updateForm(value) {
      return setForm((prev) => {
        return { ...prev, ...value };
      });
    }
  
    async function onSubmit(e) {
      e.preventDefault();
      const newPerson = { ...form, rut: form.rut };
  
      try {
        await fetch("http://localhost:5000/record/add", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newPerson),
        });
      } catch (error) {
        window.alert(error);
        return;
      }
  
      setForm({ name: "", position: "", level: "", rut: "" });
      navigate("/");
    }
  
    async function searchByRut(rut) {
      try {
        const response = await fetch(`http://localhost:5000/record/search?rut=${rut}`);
        const data = await response.json();
        // Hacer algo con los registros encontrados en "data"
      } catch (error) {
        console.error("Error al realizar la búsqueda:", error);
      }
    }
}
