import React, { useState } from "react";
import { useNavigate } from "react-router";

const Ficha = (props) => (
  <tr>
    <td></td>
  </tr>
);


export default function Visualizar() {
    const [form, setForm] = useState({
      rut: "",
    });
    const [searchResults, setSearchResults] = useState([]); // Agregar el estado searchResults
    const navigate = useNavigate();
  
    function updateForm(value) {
      return setForm((prev) => {
        return { ...prev, ...value };
      });
    }
  
    async function onSubmit(e) {
      e.preventDefault();
      const newPerson = { ...form, rut: form.rut };
  
      try {
        await fetch("http://localhost:5000/paciente/add", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newPerson),
        });
      } catch (error) {
        window.alert(error);
        return;
      }
  
      setForm({ name: "", position: "", level: "", rut: "" });
      navigate("/");
    }
  
    async function searchByRut(rut) {
      try {
        const response = await fetch(`http://localhost:5000/visualizar/search?rut=${rut}`);
        const data = await response.json();
        console.log(data);
        setSearchResults(data);
      } catch (error) {
        console.error("Error al realizar la búsqueda:", error);
      }
    }
  
    return (
      <div>
        <form onSubmit={onSubmit}>
          <label>
            Name:
            <input
              type="text"
              value={form.name}
              onChange={(e) => updateForm({ name: e.target.value })}
            />
          </label>
          <br />
          <label>
            Position:
            <input
              type="text"
              value={form.position}
              onChange={(e) => updateForm({ position: e.target.value })}
            />
          </label>
          <br />
          <label>
            Level:
            <input
              type="text"
              value={form.level}
              onChange={(e) => updateForm({ level: e.target.value })}
            />
          </label>
          <br />
          <label>
            RUT:
            <input
              type="text"
              value={form.rut}
              onChange={(e) => updateForm({ rut: e.target.value })}
            />
          </label>
          <br />
          <button type="submit">Submit</button>
        </form>
  
        <div>
          <h3>Search Results:</h3>
          <ul>
            {searchResults.map((result, index) => (
              <li key={index}>{result.name} - {result.position}</li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
