import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";


// Crear los elementos HTML del formulario de inicio de sesión
var container = document.createElement('div');
container.className = 'container';

var heading = document.createElement('h2');
heading.textContent = 'Iniciar sesión';

var form = document.createElement('form');
form.id = 'login-form';

var usernameGroup = document.createElement('div');
usernameGroup.className = 'form-group';

var usernameLabel = document.createElement('label');
usernameLabel.textContent = 'Nombre de usuario:';

var usernameInput = document.createElement('input');
usernameInput.type = 'text';
usernameInput.name = 'username';
usernameInput.required = true;

var passwordGroup = document.createElement('div');
passwordGroup.className = 'form-group';

var passwordLabel = document.createElement('label');
passwordLabel.textContent = 'Contraseña:';

var passwordInput = document.createElement('input');
passwordInput.type = 'password';
passwordInput.name = 'password';
passwordInput.required = true;

var submitGroup = document.createElement('div');
submitGroup.className = 'form-group';

var submitButton = document.createElement('input');
submitButton.type = 'submit';
submitButton.value = 'Iniciar sesión';

// Agregar los elementos al formulario
usernameGroup.appendChild(usernameLabel);
usernameGroup.appendChild(usernameInput);

passwordGroup.appendChild(passwordLabel);
passwordGroup.appendChild(passwordInput);

submitGroup.appendChild(submitButton);

form.appendChild(usernameGroup);
form.appendChild(passwordGroup);
form.appendChild(submitGroup);

container.appendChild(heading);
container.appendChild(form);

// Agregar el formulario al cuerpo del documento
document.body.appendChild(container);

// Agregar un evento de escucha al formulario para capturar los datos de inicio de sesión
form.addEventListener('submit', function(event) {
  event.preventDefault();
  
  var username = usernameInput.value;
  var password = passwordInput.value;
  
  // Añade aquí tu lógica de autenticación y otras acciones necesarias
  
  // Ejemplo de lógica de autenticación básica
  if (username === 'admin' && password === 'password') {
    alert('Inicio de sesión exitoso');
  } else {
    alert('Credenciales inválidas');
  }
  
  // Limpia los campos del formulario
  usernameInput.value = '';
  passwordInput.value = '';
});
