import React, { useState } from "react";
import { useNavigate } from "react-router";

mongoose.connect("mongodb://localhost:3000/mydatabase", {
  useNewUrlParser: true,
  useUnifiedTopology: true,
});

// Definición del esquema y el modelo para la colección
const personSchema = new mongoose.Schema({
  name: String,
  position: String,
  level: String,
  numero_de_folio: String,
  horario_de_atencion: String,
});

const Person = mongoose.model("Person", personSchema);
  
  export default function Create() {
    const [form, setForm] = useState({
      name: "",
      position: "",
      level: "",
      rut: "",
    });
    const navigate = useNavigate();
  
    function updateForm(value) {
      return setForm((prev) => {
        return { ...prev, ...value };
      });
    }
  
    async function onSubmit(e) {
      e.preventDefault();
  
      // Consulta a la base de datos para obtener los atributos "numero_de_folio" y "horario_de_atencion"
      const foundPerson = await Person.findOne({ RUT: form.rut }).exec();
  
      if (foundPerson) {
        console.log("Número de folio:", foundPerson.numero_de_folio);
        console.log("Horario de atención:", foundPerson.horario_de_atencion);
      } else {
        console.log("No se encontró ninguna persona con ese RUT.");
      }
  
      setForm({ name: "", position: "", level: "", rut: "" });
      navigate("/");
    }
  
    return (
      <div>
        <h3>Create New Record</h3>
        <form onSubmit={onSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              className="form-control"
              id="name"
              value={form.name}
              onChange={(e) => updateForm({ name: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label htmlFor="position">Position</label>
            <input
              type="text"
              className="form-control"
              id="position"
              value={form.position}
              onChange={(e) => updateForm({ position: e.target.value })}
            />
          </div>
          <div className="form-group">
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                name="positionOptions"
                id="positionIntern"
                value="Intern"
                checked={form.level === "Intern"}
                onChange={(e) => updateForm({ level: e.target.value })}
              />
              <label htmlFor="positionIntern" className="form-check-label">Intern</label>
            </div>
            {/* Resto de opciones de nivel */}
          </div>
          <div className="form-group">
            <label htmlFor="rut">RUT</label>
            <input
              type="text"
              className="form-control"
              id="rut"
              value={form.rut}
              onChange={(e) => updateForm({ rut: e.target.value })}
            />
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Create person"
              className="btn btn-primary"
            />
          </div>
      </form>
    </div>
  );
}
