import React, { useState } from "react";
import { Route, Routes, useLocation } from "react-router-dom";
import Navbar from "./components/navbar";
import RecordList from "./components/recordList";
import Edit from "./components/edit";
import Create from "./components/create";
import CrearFormulario from "./components/crearFormulario";
import Visualizar from "./components/visualizar";
import Login from "./components/login";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();

  // Función para cambiar el estado de isLoggedIn
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Comprueba si la ubicación actual es la página de inicio ("/")
  const isHomePage = location.pathname === "/";

  return (
    <div>
      {isHomePage && !isLoggedIn && <Login onLogin={handleLogin} />}
      {isLoggedIn && <Navbar />}
      <Routes>
        <Route path="/edit/:id" element={<Edit />} />
        <Route path="/create" element={<Create />} />
        <Route path="/nuevoFormulario" element={<CrearFormulario />} />
        <Route path="/visualizar/:rut" element={<Visualizar />} />
      </Routes>
    </div>
  );
};

export default App;
